export function mapValues(m, f) {
    const res = new Map();
    for (const [k, u] of m) {
        res.set(k, f(u, k));
    }
    return res;
}
export function groupBy(xs, f, opts) {
    const ignoreNonExistentKeys = !!opts && opts.ignoreNonExistentKeys;
    const res = new Map();
    for (const x of xs) {
        const key = f(x);
        if (ignoreNonExistentKeys && key == null) {
            continue;
        }
        const group = res.get(key);
        if (group) {
            group.push(x);
        }
        else {
            res.set(key, [x]);
        }
    }
    return res;
}
