export function getDimensions(fit, containerRatio, thumbnailRatio) {
    if (fit === 'contain') {
        return getContainDimensions(containerRatio, thumbnailRatio);
    }
    return getContainCropDimensions(containerRatio, thumbnailRatio);
}
export function getContainDimensions(containerRatio, thumbnailRatio) {
    if (thumbnailRatio > containerRatio) {
        return { width: '100%', height: `${(100 * containerRatio) / thumbnailRatio}%` };
    } else {
        return { width: `${(100 * thumbnailRatio) / containerRatio}%`, height: '100%' };
    }
}
export function getContainCropDimensions(containerRatio, thumbnailRatio) {
    if (thumbnailRatio > 100 / 15) {
        return {
            width: '100%',
            height: `${(100 * containerRatio) / (100 / 15)}%`,
        };
    } else if (thumbnailRatio > containerRatio) {
        return { width: '100%', height: `${(100 * containerRatio) / thumbnailRatio}%` };
    }
    else if (thumbnailRatio >= 1) {
        return { width: `${(100 * thumbnailRatio) / containerRatio}%`, height: '100%' };
    }
    else {
        return { width: '75%', height: `${100 / thumbnailRatio}%` };
    }
}
